document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".redirect-link"),n=document.getElementById("loading-spinner");n.classList.add("hidden"),e.forEach(t=>{t.addEventListener("click",function(d){d.preventDefault(),n.classList.remove("hidden"),setTimeout(()=>{window.location.href=t.href},100)})}),$("form").on("submit",function(){n.classList.remove("hidden"),$('button, input[type="submit"]').prop("disabled",!0)})});window.addEventListener("pageshow",function(e){e.persisted&&document.getElementById("loading-spinner").classList.add("hidden")});
